mapper.select:
SELECT m.MEMBER_NO, m.MEMBER_ID, m.MEMBER_NAME, m.MEMBER_BIRTH, m2.CODE_NAME AS MEMBER_GENDER, m3.CODE_NAME AS MEMBER_LV, m.MEMBER_TEL, m.MEMBER_EMAIL, m.MEMBER_ADDR, m1.CODE_NAME AS MEMBER_GRAD, m4.CODE_NAME AS MEMBER_ST, TO_CHAR(m.MEMBER_HIRE, 'YYYY-MM-DD') AS MEMBER_HIRE FROM MEMBER m LEFT JOIN MASTER_CODETBL m1 ON m.MEMBER_GRAD = m1.CODE_ID LEFT JOIN MASTER_CODETBL m2 ON m.MEMBER_GENDER = m2.CODE_ID LEFT JOIN MASTER_CODETBL m3 ON m.MEMBER_LV = m3.CODE_ID LEFT JOIN MASTER_CODETBL m4 ON m.MEMBER_ST = m4.CODE_ID LEFT JOIN GRADUATE gr ON gr.MEMBER_NO = m.MEMBER_NO WHERE 1=1
AND m.MEMBER_ST = #{memberSt}
AND m.MEMBER_GENDER = #{memberGender}
AND m.MEMBER_LV = #{memberLv}
AND (UPPER(m.MEMBER_NAME) LIKE '%' || UPPER(#{memberName}) || '%')
AND m.MEMBER_HIRE >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND m.MEMBER_HIRE <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
ORDER BY m.MEMBER_NO ASC

UPDATE MEMBER SET
MEMBER_NO = #{memberNo}

	,MEMBER_GENDER = #{memberGender}


	,MEMBER_TEL = #{memberTel}


	,MEMBER_EMAIL = #{memberEmail}


	,MEMBER_HIRE = #{memberHire}


	,MEMBER_ADDR = #{memberAddr}


	,MEMBER_LV = #{memberLv}


	,MEMBER_GRAD = #{memberGrad}

WHERE MEMBER_NO = #{memberNo}
